import React, { useState, useEffect,useCallback } from "react";
import { useNavigate } from 'react-router-dom';
import { Link, Outlet } from "react-router-dom";
import '../App.css';
import ReactDOM from "react-dom/client";
import sortJobsDetailsByAreaOrCity from './logic/sort'
export default function Drop_Down(props) {
    const [dropDownArray, setDropDownArray] = useState([])
    const [dropDownType, setDropDownType] = useState("")
    // const [onChangeFunction, setOnChangeFunction] = useState("")

function dropDownOnchange(value,dropDownType){
    console.log("hyy")
    let key=["store_region","city"]
    let index=0;
     let sortedArray = []
     switch (dropDownType) {
         case "area":
             sortedArray = sortJobsDetailsByAreaOrCity(value, key[0], props.jobsDetails);
             index=0;
            //  setCities(getArrayByKey("city", jobsDetails)) //Get arrays of cities.

             break;
         case "city":
             sortedArray = sortJobsDetailsByAreaOrCity(value,key[1] , props.jobsDetails);
             index=1;
            //  setCities(getArrayByKey("city", jobsDetails)) //Get arrays of cities.
             break;

         default:
             break;
     }
     console.log("sortedArray" + sortedArray.toString())
    //  return sortedArray
    // let sortedArray=sortJobsDetailsByAreaOrCity(value,dropDownType,props.jobsDetails)
    props.onChangeFunction(sortedArray)
    props.setFunc(key[index], props.jobsDetails)
}
const defineDropDown = async () => {
            setDropDownArray(await props.dropDownArray);
            // setOnChangeFunction(await props.onChangeFunction)
            setDropDownType(await props.dropDownType)
        };
    useEffect(() => {
        
        defineDropDown();
    }, [props.dropDownArray]);
    return (
        <div>
            <form name="form1" id="form1" action="/action_page.php">

                <select name="subject" id="subject" onChange={(e)=>dropDownOnchange(e.target.value,dropDownType)}>
                    {
                        dropDownArray.map((option)=>(
                            <option key={option} value={option}>{option}</option>
                        ))
                    }
                </select>
            </form>
        </div>);
}
