import React, { useState, useEffect } from "react";
import { useNavigate } from 'react-router-dom';
import SearchAndSort from './SearchAndSort'
export default function Table(props) {
    const [titles, setTitles] = useState([])
    const [jobsDetails, setJobsDetails] = useState(props.apiSort ?  props.apiSort : JSON.parse(sessionStorage.getItem("currentTable")).response)
    const defineTitleArray = async () => {
        setJobsDetails(props.apiSort ? await props.apiSort : jobsDetails)
        let titlesArray = [];
        for (const key in jobsDetails[0]) {
            titlesArray.push(key);
        }
        setTitles(titlesArray);

    };
    useEffect(() => {
        defineTitleArray();
    }, [jobsDetails]);

    return (<div >
        <table border="1px">
            <thead>
                <tr>
                    {
                        setJobsDetails(props.apiSort ?  props.apiSort : jobsDetails)
                    }
                    {
                        jobsDetails ? titles.map((title, i) =>
                        (
                            < th key={new Date() + title}>{title}</th>
                        )) :
                            < th ></th>

                    }

                </tr>
            </thead>
            {jobsDetails ? jobsDetails.map((jobDetail, i) =>
            (<tr key={new Date() + i}>
                {titles.map((title) => (
                    <td key={new Date() + i + title}>"{String(jobDetail[title])}"</td>
                ))}
            </tr>
            )
            ) : <tr></tr>}



        </table>
    </div >);
}
