import React, { useState, useEffect } from "react";
import { useParams } from 'react-router-dom';
import { useNavigate } from 'react-router-dom';
import { useLocation } from 'react-router-dom';
import { Routes, Route, Link, Outlet } from 'react-router-dom';
import Drop_Down from "./Drop_Down";
import getArrayByKey from'./logic/getArrayByKey'
export default function SearchAndSort(props) {
    const [areas, setAreas] = useState([])
    const [cities, setCities] = useState([])
    function setStates(){
        setAreas(getArrayByKey("store_region",props.jobsDetails));//Get array of areas.
        setCities(getArrayByKey("city",props.jobsDetails))//Get arrays of cities.
    }
    useEffect(() => {
        setStates()
    });
    return (<div >
        <div>
            <p >search by name of shoe: </p>
            <input type='text' placeholder='search..' />

            <Drop_Down dropDownArray={props.areas} setFunc={setAreas} dropDownType="area" jobsDetails={props.jobsDetails} onChangeFunction={() => props.setJobsDetails} />
            <Drop_Down dropDownArray={props.cities} setFunc={setCities} dropDownType="city" jobsDetails={props.jobsDetails} onChangeFunction={() => props.setJobsDetails} />
        </div>
    </div>);
}
