import React, { useState, useEffect, useCallback } from "react";
import { useNavigate } from 'react-router-dom';
import { Link, Outlet } from "react-router-dom";
import '../App.css';
import ReactDOM from "react-dom/client";
import saveDropDownValues from './logic/sort'
export default function Drop_Down(props) {
    const [dropDownArray, setDropDownArray] = useState([])
    const [dropDownType, setDropDownType] = useState("")
    function dropDownOnchange(value, dropDownType) {
        let key = ["store_region", "city"]
        let index = 0;
        let sortedArray = []
        switch (dropDownType) {
            case "area":
                sortedArray = saveDropDownValues(value, key[0], props.jobsDetails);
                index = 0;

                break;
            case "city":
                sortedArray = saveDropDownValues(value, key[1], props.jobsDetails);
                index = 1;
                break;

            default:
                break;
        }
        console.log("sortedArray" + sortedArray.toString())
        props.setJobsDetails(sortedArray)
        sessionStorage.setItem("currentTable", JSON.stringify({response:sortedArray.flat()}))
        props.setDropDownArray(sortedArray)
        setDropDownArray(sortedArray)
    }
    const defineDropDown = async () => {
        setDropDownArray( props.dropDownArray);
        setDropDownType( props.dropDownType)
    };
    useEffect(() => {

        defineDropDown();
    }, [props.dropDownArray]);
    return (
        <div>
            <form name="form1" id="form1" action="/action_page.php">

                <select name="subject" id="subject" onChange={(e) => dropDownOnchange(e.target.value, dropDownType)}>
                    {
                        
                        props.dropDownArray.map((option) => (
                            <option key={option} value={option}>{option}</option>
                            
                        ))

                    }
                    {console.log(props.dropDownArray)}
                </select>
            </form>
        </div>);
}
