import React, { useState, useEffect,useCallback } from "react";
import { useNavigate } from 'react-router-dom';
import { Link, Outlet } from "react-router-dom";
import '../App.css';
import ReactDOM from "react-dom/client";
export default function Drop_Down(props) {
    const [dropDownArray, setDropDownArray] = useState([])
    const [dropDownType, setDropDownType] = useState("")
    // const [onChangeFunction, setOnChangeFunction] = useState("")


    useEffect(() => {
        const defineDropDown = async () => {
            setDropDownArray(await props.dropDownArray);
            // setOnChangeFunction(await props.onChangeFunction)
            setDropDownType(await props.dropDownType)
        };
        defineDropDown();
    }, [props.dropDownArray]);
    return (
        <div>
            <form name="form1" id="form1" action="/action_page.php">

                <select name="subject" id="subject" onChange={(e)=>props.onChangeFunction(e.target.value,dropDownType)}>
                    {
                        dropDownArray.map((option)=>(
                            <option key={option} value={option}>{option}</option>
                        ))
                    }
                </select>
            </form>
        </div>);
}
