 function sortJobsDetailsByAreaOrCity(value, key, jobsDetails) {
     return jobsDetails.filter(job => job[key].trim() === value.trim());

     // window.location.reload()
 }

 export default function saveDropDownValues(value, type, jobsDetails) {
     // alert("value: " + value + " type" + type);
     console.log("hyy")
     let sortedArray = []
     switch (type) {
         case "area":
             sortedArray = sortJobsDetailsByAreaOrCity(value, "store_region", jobsDetails);
             //  setCities(getArrayByKey("city", jobsDetails)) //Get arrays of cities.

             break;
         case "city":
             sortedArray = sortJobsDetailsByAreaOrCity(value, "city", jobsDetails);
             //  setCities(getArrayByKey("city", jobsDetails)) //Get arrays of cities.
             break;

         default:
             break;
     }
     console.log("sortedArray" + sortedArray.toString())
     return sortedArray


 };
