import React, { useState, useMemo, useEffect, useCallback } from "react";
import { useParams } from 'react-router-dom';
import { useNavigate } from 'react-router-dom';
import { useLocation } from 'react-router-dom';
import { Routes, Route, Link, Outlet } from 'react-router-dom';
import Table from "./Table";
import Drop_Down from "./Drop_Down";
import sortJobsDetailsByAreaOrCity from './logic/sort'
import SearchAndSort from './SearchAndSort'
import getArrayByKey from './logic/getArrayByKey'
import searchByInput from "./logic/searchByInput";
export default function Main_Component(props) {
    const [searchSort, setSearchSort] = useState([])
    const [citySort, setCitySort] = useState([])
    const [areaSort, setAreaSort] = useState([])
    const [apiSort, setApiSort] = useState(JSON.parse(sessionStorage.getItem("currentTable")) ? JSON.parse(sessionStorage.getItem("currentTable")).response : props.apiSort)
    const [searchInputValue, setSearchInputValue] = useState([])

    const [jobsDetails, setJobsDetails] = useState([])
    const [areas, setAreas] = useState([])
    const [cities, setCities] = useState([])
    async function setStates() {
        setAreas(getArrayByKey("store_region", await props.apiSort));//Get array of areas.
        setCities(getArrayByKey("city", jobsDetails?jobsDetails:await props.apiSort))//Get arrays of cities.
        setJobsDetails(jobsDetails.length>0?jobsDetails:await props.apiSort)

    }
    function inputValueSearch(value) {
        setSearchInputValue(value)
        let list = searchByInput(value, JSON.parse(sessionStorage.getItem("currentTable")).response)
        setJobsDetails(list)
        setJobsDetails(list)


    }
    // const memoizedArray = useMemo(() => generateDropDownArray(), [jobsDetails]);
    function setJobs(val) {
        setJobsDetails(val)
    }
    useEffect(() => {
        setStates()
    }, [jobsDetails, props.apiSort]);
    return (<div>
        <header>
            <h1 className="h1">JobLocator</h1>
        </header>
        <div>
            <p >search by name of shoe: </p>
            <input type='text' value={searchInputValue} onChange={(e) => inputValueSearch(e.target.value)} placeholder='search..' />

            <Drop_Down dropDownArray={areas} setDropDownArray={setCities} dropDownType="area" jobsDetails={jobsDetails} setJobsDetails={setJobs} />
            <Drop_Down dropDownArray={cities} setDropDownArray={setCities} dropDownType="city" jobsDetails={jobsDetails} setJobsDetails={setJobs} />
        </div>

        <Table jobsDetails={jobsDetails}></Table>
    </div>);
}
