import React, { useState, useEffect, useCallback } from "react";
import { useParams } from 'react-router-dom';
import { useNavigate } from 'react-router-dom';
import { useLocation } from 'react-router-dom';
import { Routes, Route, Link, Outlet } from 'react-router-dom';
import Table from "./Table";
import Drop_Down from "./Drop_Down";
export default function Main_Component(props) {
    const [jobsDetails, setJobsDetails] = useState([])
    const [areas, setAreas] = useState([])
    const [cities, setCities] = useState([])
    function sortJobsDetailsByAreaOrCity(area,key) {
        const set = new Set();
        for (let job of jobsDetails) {
            if (job[key] === area) {
                set.add(job[key]);
            }
        }
        setJobsDetails([...set]);
    }
    function sortJobsDetailsByArea(area) {
        const set = new Set();
        for (let job of jobsDetails) {
            if (job["store_region"] === area) {
                set.add(job[key]);
            }
        }
        setJobsDetails([...set]);
    }
    const saveDropDownValues = useCallback((value, type) => {
        alert("value: " + value + " type" + type);
        switch (type) {
            case "area":
                sortJobsDetailsByAreaOrCity(value,"store_region");
                setCities(getArrayByKey("city"))//Get arrays of cities.

                break;
            case "city":
                sortJobsDetailsByAreaOrCity(value,"city");
                setCities(getArrayByKey("city"))//Get arrays of cities.
                break;

            default:
                break;
        }

    }, []);
    async function apiCall() {//Gets all data from api.
        try {
            let response = await fetch('http://localhost:3003/api/getAll');
            response = await response.json()
            setJobsDetails(response);
        } catch (error) {
            console.error(error);
        }
    }
    function getArrayByKey(key) {//Get array of certain key.
        const set = new Set();
        for (let job of jobsDetails) {
            set.add(job[key]);
        }

        return [...set];
    }
    useEffect(() => {
        apiCall();//Get all data from api.
        setAreas(getArrayByKey("store_region"));//Get array of areas.
        setCities(getArrayByKey("city"))//Get arrays of cities.
    }, [jobsDetails]);
    return (<div>
        <header>
            <h1 className="h1">JobLocator</h1>
        </header>
        <div>
            <p >search by name of shoe: </p>
            <input type='text' placeholder='search..' />

            <Drop_Down dropDownArray={areas} dropDownType="area" onChangeFunction={() => saveDropDownValues} />
            <Drop_Down dropDownArray={cities} dropDownType="city" onChangeFunction={() => saveDropDownValues} />
        </div>
        <Table jobsDetails={jobsDetails}></Table>
    </div>);
}
