import React, { useState, useEffect, useCallback } from "react";
import { useParams } from 'react-router-dom';
import { useNavigate } from 'react-router-dom';
import { useLocation } from 'react-router-dom';
import { Routes, Route, Link, Outlet } from 'react-router-dom';
import Table from "./Table";
import Drop_Down from "./Drop_Down";
import sortJobsDetailsByAreaOrCity from './logic/sort'
import SearchAndSort from './SearchAndSort'
import getArrayByKey from'./logic/getArrayByKey'

export default function Main_Component(props) {
    const [searchSort, setSearchSort] = useState([])
    const [citySort, setCitySort] = useState([])
    const [areaSort, setAreaSort] = useState([])
    const [apiSort, setApiSort] = useState(props.apiSort)

    const [jobsDetails, setJobsDetails] = useState([])
    const [areas, setAreas] = useState([])
    const [cities, setCities] = useState([])
   
    useEffect(() => {
        setCities(getArrayByKey("city",jobsDetails))//Get arrays of cities.
        setJobsDetails(props.apiSort)
        setAreas(getArrayByKey("store_region",props.apiSort));//Get array of areas.


    }, [props.apiSort]);
    return (<div>
        <header>
            <h1 className="h1">JobLocator</h1>
        </header>
        <div>
            <p >search by name of shoe: </p>
            <input type='text' placeholder='search..' />

            <Drop_Down dropDownArray={areas} setDropDownArray={setAreas} dropDownType="area" jobsDetails={jobsDetails} setJobsDetails={() => props.setJobsDetails} />
            <Drop_Down dropDownArray={cities} setDropDownArray={setCities} dropDownType="city" jobsDetails={props.jobsDetails} setJobsDetails={() => props.setJobsDetails} />
        </div>
        {/* <SearchAndSort cities={props.cities} areas={props.areas} jobsDetails={jobsDetails} setJobsDetails={setJobsDetails}></SearchAndSort> */}

        <Table areas={areas} cities={cities} ></Table>
    </div>);
}
