import React, { useState, useEffect } from "react";
import { useParams } from 'react-router-dom';
import { useNavigate } from 'react-router-dom';
import { useLocation } from 'react-router-dom';
import { Routes, Route, Link, Outlet } from 'react-router-dom';
import Table from "./Table";
import Drop_Down from "./Drop_Down";
export default function Main_Component(props) {
    const [jobsDetails, setJobsDetails] = useState([])
    const [areas, setAreas] = useState([])
    const [cities, setCities] = useState([])


    async function apiCall() {//Gets all data from api.
        try {
            let response = await fetch('http://localhost:3003/api/getAll');
            response = await response.json()
            setJobsDetails(response);
        } catch (error) {
            console.error(error);
        }
    }
    function getArrayByKey(key) {//Get array of certain key.
        let array=[];
        for (let job in jobsDetails) {
           array.push(job[key]);
        }
        return array
    }
    useEffect(() => {
        apiCall();//Get all data from api.
        setAreas(getArrayByKey("store_region"));//Get array of areas.
        setCities(getArrayByKey("city"))//Get arrays of cities.
    }, [jobsDetails]);
    return (<div>
        <header>
            <h1 className="h1">JobLocator</h1>
        </header>
        <div>
            <p >search by name of shoe: </p>
            <input type='text' placeholder='search..' />

            <Drop_Down dropDownArray={areas} dropDownType="area" onChangeFunction={setJobsDetails} />
            <Drop_Down dropDownArray={cities} dropDownType="area" onChangeFunction={setJobsDetails} />
        </div>
        <Table jobsDetails={jobsDetails}></Table>
    </div>);
}
