 function sortJobsDetailsByAreaOrCity(value, key, jobsDetails) {
     return jobsDetails.filter(job => job[key].trim() === value.trim());

     // window.location.reload()
 }

 function sortByAttribute(key, jobsDetails) {
     let response = []
     jobsDetails.forEach(job =>
         response.push(job[key])
     );
     return response
         // window.location.reload()
 }
 export default function saveDropDownValues(value, type, jobsDetails) {
     // alert("value: " + value + " type" + type);
     //  console.log("hyy")
     let objectsList = []
     let attributesList = []
     switch (type) {
         case "store_region":
             objectsList = sortJobsDetailsByAreaOrCity(value, "store_region", jobsDetails);
             attributesList = sortByAttribute("store_region", jobsDetails)
                 //  setCities(getArrayByKey("city", jobsDetails)) //Get arrays of cities.

             break;
         case "city":
             objectsList = sortJobsDetailsByAreaOrCity(value, "city", jobsDetails);
             attributesList = sortByAttribute("city", jobsDetails)

             //  setCities(getArrayByKey("city", jobsDetails)) //Get arrays of cities.
             break;

         default:
             break;
     }

     console.log("sortedArray" + objectsList.toString())
     return [objectsList, attributesList]


 };
