import React, { useState, useEffect } from "react";
import { useParams } from 'react-router-dom';
import { useNavigate } from 'react-router-dom';
import { useLocation } from 'react-router-dom';
import { Routes, Route, Link, Outlet } from 'react-router-dom';
import Drop_Down from "./Drop_Down";
import getArrayByKey from'./logic/getArrayByKey'
import Drop_Down_Area from "./Drop_Down_Area";
export default function SearchAndSort(props) {
    const [areas, setAreas] = useState([])
    const [cities, setCities] = useState([])
    const [check, setcheck] = useState(false)

    function setStates(){
        setcheck(true)
        let jobsDetails=JSON.parse(sessionStorage.getItem("currentTable")).response
        setAreas(getArrayByKey("store_region",jobsDetails));//Get array of areas.
        setCities(getArrayByKey("city",jobsDetails))//Get arrays of cities.
        console.log("props.jobsDetails1 " + jobsDetails)

    }
    useEffect(() => {
       setStates()
    },[props.jobsDetails]);
    return (<div >
        <div>
            <p >search by name of shoe: </p>
            <input type='text' placeholder='search..' />

            <Drop_Down_Area dropDownArray={areas} setFunc={setAreas} dropDownType="area" jobsDetails={props.jobsDetails} onChangeFunction={() => props.setJobsDetails} />
            <Drop_Down dropDownArray={cities} setFunc={setCities} dropDownType="city" jobsDetails={props.jobsDetails} onChangeFunction={() => props.setJobsDetails} />
        </div>
    </div>);
}
