import React, { useState, useEffect } from "react";

// import { useNavigate } from 'react-router-dom';
// import { Link, Outlet } from "react-router-dom";
import '../App.css';
// import ReactDOM from "react-dom/client";

import saveDropDownValues from './logic/sort'
export default function Drop_Down(props) {
    // const [dropDownArray, setDropDownArray] = useState([])
    const [dropDownType, setDropDownType] = useState("")
    const [defaultSelectedValue, setDefaultSelectedValue] = useState("")
   

    function dropDownOnchange(value, dropDownType) {
       
        let key = ["store_region", "city"]
        let objectList = []
        let attributeList = []
        setDefaultSelectedValue(value)

        switch (dropDownType) {
            case "area":
                [objectList,attributeList] = saveDropDownValues(value, key[0], JSON.parse(sessionStorage.getItem("currentTable")).response);

                break;
            case "city":
                [objectList,attributeList] = saveDropDownValues(value, key[1], props.jobsDetails);
                break;

            default:
                break;
        }
        props.setJobsDetails(objectList)
        props.setDropDownArray(attributeList)
        // props.dropDownType==="area"?setDropDownArray(attributeList):setDropDownArray( props.dropDownArray);
    }
    const defineDropDown = async () => {
        // setDropDownArray( props.dropDownArray);
        setDropDownType( props.dropDownType)
    };
    useEffect(() => {
        defineDropDown();
    }, [props.dropDownArray]);
    return (
        <div>
            <form name="form1" id="form1" action="/action_page.php" >
                <label>{dropDownType}:</label>
          <select  className={dropDownType} onChange={(e) => dropDownOnchange(e.target.value, dropDownType)}>
               
                    {
                        
                        props.dropDownArray.map((option) => (
                            <option key={option+new Date()}  selected={option === defaultSelectedValue} value={option}>{option}</option>
                           
                        ))

                    }
                    {console.log(props.dropDownArray)}
                </select>
            </form>
        </div>);
}
