import React, { useState, useEffect } from "react";
import { useNavigate } from 'react-router-dom';
import { Link, Outlet } from "react-router-dom"
import '../App.css';
// import image from "../img/menue.jpg"
import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap/dist/css/bootstrap.css';
// import { FontAwesomeIcon } from '../img/black_vi.jpg';
// import SignOut from './Log_Out'
// import check from './logic/search'

export default function Shoe_Size_Table(props) {
    const [code, setCode] = useState(props.code)
    const [shoes, set_Shoes] = useState([])
    const [total, set_Total] = useState(0)

    async function get_shoe_sizes() {
        let shoeSizesArray = await fetch(`http://localhost:3678/quantityofsize/get/${code}`, {
            method: 'GET',
        });
        shoeSizesArray = await shoeSizesArray.json();
        return shoeSizesArray
    }
    useEffect(() => {
        
        return async () => {
            let sum=0;
            let shoe_sizes=await get_shoe_sizes()
            set_Shoes(shoe_sizes)
             shoe_sizes.map((shoe)=>{
                sum+=shoe.Quantity
                 set_Total(sum)
             })
        }
    }, []);

    return (
        <div>
            <table border="1px">
                <thead>
                    <tr>
                        <th className="th">Size</th>
                        <th className="th">Amount</th>
                    </tr>
                </thead>
                {shoes.map((shoe) =>
                (
                    <tr key={shoe}>

                        <td contentEditable>{shoe.ShoeSize}</td>
                        <td contentEditable>{shoe.Quantity}</td>
                        <button className="but2 table_btn" >delete</button>
                    </tr>
                )
                )}


                <tr>
                    <th className="th">total:</th>
                    <td>{total}</td>
                </tr>

            </table>
            
            <table>
            <thead>
                    <tr>
                        <th className="th" placeholder="Size"></th>
                        <th className="th" placeholder="Amount"></th>
                    </tr>
                </thead>
            </table>
            <button className="but2 table_btn" style={{ width: "35%" }}>add size</button>

        </div>)
};
