import React, { useState, useEffect, useCallback } from "react";
import { useParams } from 'react-router-dom';
import { useNavigate } from 'react-router-dom';
import { useLocation } from 'react-router-dom';
import { Routes, Route, Link, Outlet } from 'react-router-dom';
import Table from "./Table";
import Drop_Down from "./Drop_Down";
import sortJobsDetailsByAreaOrCity from './logic/sort'
export default function Main_Component(props) {
    const [searchSort, setSearchSort] = useState([])
    const [citySort, setCitySort] = useState([])
    const [areaSort, setAreaSort] = useState([])
    const [apiSort, setApiSort] = useState(props.apiSort)

    const [jobsDetails, setJobsDetails] = useState([])
    const [areas, setAreas] = useState([])
    const [cities, setCities] = useState([])
    // function sortJobsDetailsByAreaOrCity(area,key) {
    //     const set = new Set();
    //     for (let job of jobsDetails) {
    //         if (job[key] === area) {
    //             set.add(job);
    //         }
    //     }
    //     setJobsDetails([...set]);
    //     // window.location.reload()
    // }

    const saveDropDownValues = useCallback((value, type) => {
        // alert("value: " + value + " type" + type);
        console.log("hyy")
        let sortedArray = []
        switch (type) {
            case "area":
                sortedArray = sortJobsDetailsByAreaOrCity(value, "store_region", jobsDetails);
                setCities(getArrayByKey("city"))//Get arrays of cities.

                break;
            case "city":
                sortedArray = sortJobsDetailsByAreaOrCity(value, "city", jobsDetails);
                setCities(getArrayByKey("city"))//Get arrays of cities.
                break;

            default:
                break;
        }
        setJobsDetails(sortedArray)
        console.log("sortedArray"+sortedArray)


    }, []);

    function getArrayByKey(key) {//Get array of certain key.
        const set = new Set();
        for (let job of jobsDetails) {
            set.add(job[key]);
        }

        return [...set];
    }
    useEffect(() => {
        // apiCall();//Get all data from api.
        // setApiSort(props.apiSort)
        setJobsDetails(props.apiSort)
        setAreas(getArrayByKey("store_region"));//Get array of areas.
        setCities(getArrayByKey("city"))//Get arrays of cities.
    }, [jobsDetails,props.apiSort]);
    return (<div>
        <header>
            <h1 className="h1">JobLocator</h1>
        </header>
        <div>
            <p >search by name of shoe: </p>
            <input type='text' placeholder='search..' />

            <Drop_Down dropDownArray={areas} dropDownType="area" onChangeFunction={() => saveDropDownValues} />
            <Drop_Down dropDownArray={cities} dropDownType="city" onChangeFunction={() => saveDropDownValues} />
        </div>
        <Table jobsDetails={jobsDetails}></Table>
    </div>);
}
